import { Button, CardMedia, Container, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import DialogBox from "../../myComponents/dialog-box/DialogBox";
import NavBar from "../../myComponents/navbar/Navbar";
import "./style.css"

export default function HomePage() {
  const navigate = useNavigate();

  const getDataFromLS = () => {
    const data = localStorage.getItem('data');
    if (data) {
      return JSON.parse(data);
    }
    else {
      return []
    }
  }


  const [data, setData] = useState(getDataFromLS());
  const [open, setOpen] = useState(false);
  const [valueid, setValue] = useState()
  const [name, setName] = useState()
  console.log(valueid);
  console.log(name);

  let total = 0;
  data?.forEach(item => {
    total = total + Number(item.value);
  });

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleRemove = (id) => {
    data.splice(id, 1)
    localStorage.setItem('data', JSON.stringify(data))
    navigate("/")
  }

  const exchangedData = {
    "id": "3",
    "name": "NewBinance",
    "value": "70",
    "image": "https://cryptologos.cc/logos/binance-coin-bnb-logo.png?v=018"
  }

  const handleSubmit = () => {
    let newData = data;
    newData.unshift(exchangedData)
    localStorage.setItem('data', JSON.stringify(newData))
    navigate("/")
  }

  const handleExchangeChange = (event, name) => {
    setName(name.props.children);
    setValue(event.target.value);
  }

  useEffect(() => {
    setData(JSON.parse(localStorage.getItem('data')));
  }, [])

  return (
    <Container>
      <NavBar />
      <Box className="header" sx={{ padding: "45px 60px", mb: "10px", borderRadius: "15px" }}>
        <Box>
          <Typography variant='small'>Number Of Crypto Coins Types</Typography> <br />
          <Typography variant='small'>{data.length || {}}</Typography>
        </Box>

        <Box>
          <Typography variant='small'>Number Of Crypto Coins</Typography> <br />
          <Typography variant='small'>
            {total}
          </Typography> <br />
        </Box>
      </Box>
      {data.length ? <Box>
        {
          data?.map((item, index) => (
            <Box key={index} className="content" sx={{ padding: "15px", mb: "10px" }}>
              <Box sx={{ display: "flex", justifyContent: "space-between", padding: "15px" }}>
                <Box sx={{ display: "flex", width: "50px" }}>
                  <CardMedia
                    sx={{ pr: "10px" }}
                    component="img"
                    height="30"
                    width="30"
                    image={item.image}
                    alt={item.name}
                  />
                  <Typography sx={{ pt: "5px" }} key={index} variant="small">{item.name} </Typography>
                </Box>
                <Box sx={{ pt: "5px" }}>
                  <Typography key={index} variant="small">
                    {item.value}
                  </Typography>
                </Box>
                <Box>
                  <Button onClick={handleClickOpen} key={index} sx={{ mr: "10px", BackgroundColor: "red" }} variant="contained" >exchange</Button>
                  <Button
                    onClick={() => handleRemove(index)}
                    variant="contained"
                  >
                    Remove
                  </Button>
                </Box>
              </Box>
            </Box>
          ))
        }
      </Box>
        :
        <Box>
          <Box className="content" sx={{ padding: "15px", mb: "10px" }}>
            <Box sx={{ display: "flex", justifyContent: "space-between", padding: "15px" }}>
              <Typography variant="small">Opps! No Crypto Coins found ):</Typography>
              <Button variant="contained" onClick={() => navigate('/crypto-page')}>Buy Crypto</Button>

            </Box>
          </Box>
        </Box>
      }
      <DialogBox
        handleClickOpen={handleClickOpen}
        handleClose={handleClose}
        handleSubmit={handleSubmit}
        selected={valueid}
        handleExchangeChange={handleExchangeChange}
        open={open}
      />
    </Container>
  )
}
