import { Button, Dialog, DialogContent, DialogContentText, DialogTitle, TextField, Typography, CardMedia, DialogActions } from "@mui/material";
import { Box } from "@mui/system";
import { useState } from "react";
import { useNavigate } from "react-router";

export default function CryptoBlock(props) {
  const navigate = useNavigate();
  const [open, setOpen] = useState(false);
  const [value, setValue] = useState();
  const [name, setName] = useState();

  const objData = { name: name, value: value }

  const data = Object.values([objData]);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleBuy = () => {
    const result = localStorage.setItem("dataCrypto", JSON.stringify(data));
    JSON.parse(result)
    handleClose();
  }

  const handleClose = () => {
    setOpen(false);
  };

  const handleChange = (e) => {
    setValue(e.target.value, props.name);
    console.log(setValue);
  };

  return (
    <>
      <Box sx={{ display: 'flex', flexDirection: 'row' }}>
        <CardMedia
          component="img"
          height="50"
          width="50"
          image="#"
          alt={props.name}
        />
        <Typography> {props.name}</Typography>
        <Button variant="outlined" onClick={handleClickOpen} >Buy</Button>
        <Dialog open={open} onClose={handleClose}>
          <DialogTitle>{props.name}</DialogTitle>
          <DialogContent>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Amount"
              type="number"
              fullWidth
              variant="standard"
              onChange={handleChange}
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button onClick={handleBuy}>Buy</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </>
  )
};
