import { Button, Dialog, DialogContent, DialogContentText, DialogTitle, TextField, Typography, CardMedia, DialogActions, FormLabel, FormControl } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router";

export default function CryptoBlock(props) {

  const [data, setData] = useState([]);

  const [value, setValue] = useState();
console.log(value)
  // const navigate = useNavigate();
  // const [data, setData] = useState([])
  // console.log(data);
  const [open, setOpen] = useState(false);
  // const [value, setValue] = useState();
  // const [name, setName] = useState();

  const handleClickOpen = () => {
    setOpen(true);
  };

  // const handleBuy = (e) => {
  //   e.preventDefault();
  //   let mydata = {
  //     value,
  //     name,
  //   }
  //   setData([...data, mydata]);
  //   setName('');
  //   setValue('');
  // }

  const handleBuy = () => {
    console.log(data);
  }

  const handleClose = () => {
    setOpen(false);
  };

  // const handleChange = (e) => {
  //   setValue(e.target.value);
  //   setName(props.name)
  // };

  // useEffect (()=> {
  //   localStorage.setItem('data', JSON.stringify(data))
  // },[data])

  function handleSubmit(e) {
    e.preventDefault();
    let mydata={
      value,
    }
    setData([...data, mydata]);
  }


  return (
    <>
      <Box sx={{ display: 'flex', flexDirection: 'row' }}>
        <CardMedia
          component="img"
          height="50"
          width="50"
          image="#"
          alt={props.name}
        />
        <Typography> {props.name}</Typography>
        <Button variant="outlined" onClick={handleClickOpen} >Buy</Button>
        <Dialog open={open} onClose={handleClose}>
          <DialogTitle>{props.name}</DialogTitle>
          <FormControl onSubmit={handleSubmit}>
            <TextField
              autoFocus
              margin="dense"
              id="name"
              label="Amount"
              type="number"
              fullWidth
              variant="standard"
              onChange={(e) => setValue(e.target.value)}
              value={value}
            />
          </FormControl>
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button onClick={handleBuy}>Buy</Button>
          </DialogActions>
        </Dialog>
      </Box>
    </>
  )
};
