import { Button, Container, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import CryptoBlock from "../../myComponents/crypto-block/CryptoBlock";
import NavBar from "../../myComponents/navbar/Navbar";

export default function CryptoPage() {

  const cryptoData = [
    "Bitcoin",
    "Gitcoin",
    "Litcoin",
    "LaudaCoin",
    "Bitcoin",
    "Gitcoin",
    "Litcoin",
    "LaudaCoin",
    "Litcoin",
    "LaudaCoin"
  ];

  const [data, setData] = useState([]);
  console.log(data);
  const [value, setValue] = useState('');
  const [name, setName] = useState('');

  function getValue(value, name) {
    setValue(value);
    setName(name);
  }

  function handleClose(e) {
    e.preventDefault();
    let mydata = {
      value,
      name,
    }
    setData([...data, mydata]);
    setName('');
    setValue('');
  }

  useEffect(() => {
    localStorage.setItem('data', data)
  }, [data])

  return (
    <>
      <Container>
        <NavBar />
        <Typography variant="h6">
          Crypto Coins
        </Typography>
        <Box>
          {
            cryptoData.map((item) => (
              <CryptoBlock value={getValue} handleClose={handleClose} name={item} />
            ))
          }
        </Box>
        {/* <Button onClick={handleClose}>Buy</Button> */}
      </Container>
    </>
  )
}
