import { Button, Container, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import NavBar from "../../myComponents/navbar/Navbar";

export default function HomePage() {

  const getDataFromLS = () => {
    const data = localStorage.getItem('data');
    if (data) {
      return JSON.parse(data);
    }
    else {
      return []
    }
  }

  const navigate = useNavigate();
  const [data, setData] = useState(getDataFromLS());

  function handleRemove(){
    localStorage.clear("data");

  }

  useEffect(() => {
    var data = JSON.parse(localStorage.getItem('data'));
    setData(data);
  }, [data])

  return (
    <Container>
      <NavBar />
      <Box className="header" sx={{ display: "flex", justifyContent: "space-between", textAlign: "center", backgroundColor: "gray", padding: "20px", mb: "10px", borderRadius: "15px" }}>
        <Box>
          <Typography variant='small'>Number Of Crypto Coins Types</Typography> <br />
          <Typography variant='small'>{data?.length}</Typography>
        </Box>
        <Box>
          <Typography variant='small'>Number Of Crypto Coins</Typography> <br />
          <Typography variant='small'>{data?.reduce(function (sum, current) {
            return sum + current.value;
          }, 0)
          }</Typography> <br />

        </Box>
      </Box>
      {
        data?.map((item, index) => (
          <Box key={index} sx={{ backgroundColor: "green", padding: "15px", mb: "10px" }}>
            <Box sx={{ display: "flex", justifyContent: "space-between", padding: "15px" }}>
              <h4>{item.name} : {item.value}</h4>
              <Box>
                <Button sx={{ mr: "10px" }} variant="contained">exchange</Button>
                <Button onClick={handleRemove} variant="contained">Remove</Button>
              </Box>
            </Box>
          </Box>        
        ))
      }
    </Container>
  )
}
