import { Button, CardMedia, Container, Dialog, DialogActions, DialogContent, DialogContentText, DialogTitle, Link, MenuItem, Select, TextField, Typography } from "@mui/material";
import { Box } from "@mui/system";
import { useEffect, useState } from "react";
import { useNavigate } from "react-router";
import cryptoData from "../../data";
import NavBar from "../../myComponents/navbar/Navbar";
import "./style.css"

export default function HomePage() {

  const getDataFromLS = () => {
    const data = localStorage.getItem('data');
    if (data) {
      return JSON.parse(data);
    }
    else {
      return []
    }
  }

  let total = 1;
  const navigate = useNavigate();
  const [data, setData] = useState(getDataFromLS());
  const [sum, setSum] = useState();

  const handleRemove = (id) => {
    data.splice(id, 1)
    localStorage.setItem('data', JSON.stringify(data))
  }

  //dialogBOx
  const [open, setOpen] = useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [value, setValue] = useState('');

  const handleChange = (event) => {
    setValue(event.target.value);
  };

  useEffect(() => {
    var data = JSON.parse(localStorage.getItem('data'));
    setData(data);
  }, [data])

  return (
    <Container>
      <NavBar />
      <Box className="header" sx={{ padding: "45px 60px", mb: "10px", borderRadius: "15px" }}>
        <Box>
          <Typography variant='small'>Number Of Crypto Coins Types</Typography> <br />
          <Typography variant='small'>{data?.length}</Typography>
        </Box>

        <Box>
          <Typography variant='small'>Number Of Crypto Coins</Typography> <br />
          <Typography variant='small'>
            {/* {
              data.forEach((item) => (
                total += item.value;
              ))
            } */}
            {data?.map((item, index) => (
              <>
                <Typography> key={index} >{ total += item.value }</Typography>
              </>
            ))}
          </Typography> <br />
        </Box>
      </Box>
      {<Box>
        {
          data?.map((item, index) => (
            <Box className="content" sx={{ padding: "15px", mb: "10px" }}>
              <Box sx={{ display: "flex", justifyContent: "space-between", padding: "15px" }}>
                <Box sx={{ display: "flex", width: "50px" }}>
                  <CardMedia
                    sx={{ pr: "10px" }}
                    component="img"
                    height="30"
                    width="30"
                    image={item.image}
                    alt={item.name}
                  />
                  <Typography sx={{ pt: "5px" }} key={index} variant="small">{item.name} </Typography>
                </Box>
                <Box sx={{ pt: "5px" }}>
                  <Typography key={index} variant="small">
                    {item.value}
                  </Typography>
                </Box>
                <Box>
                  <Button key={index} sx={{ mr: "10px", BackgroundColor: "red" }} variant="contained" onClick={handleClickOpen} >exchange</Button>
                  <Button
                    onClick={() => handleRemove(index)}
                    variant="contained"
                  >
                    Remove
                  </Button>
                </Box>
              </Box>
            </Box>
          ))
        }
      </Box>}
      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>Exchange {data[0]?.name} with</DialogTitle>
        <DialogContent>
          <Select
            fullWidth
            labelId="demo-simple-select-label"
            id="demo-simple-select"
            value={setValue}
            label="Choose Crypto"
            onChange={handleChange}
          >
            {cryptoData?.map((item, index) => (
              <>
                <MenuItem key={index} value={10}>{item.name}</MenuItem>
              </>
            ))}
          </Select>
          <TextField
            autoFocus
            margin="dense"
            label="Crypto Quantity"
            type="email"
            fullWidth
            variant="standard"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Cancel</Button>
          <Button onClick={handleClose}>Submit</Button>
        </DialogActions>
      </Dialog>
    </Container>
  )
}
